import React, { Component } from 'react'
import './OrderSummary.css'

class OrderSummary extends Component {

  componentDidUpdate() {
    console.log('OrderSummary')
  }

  render() {

    // The Object.keys() method converts an object into an array of keys
    const ingredientsArr = Object.keys(this.props.ingredients)

    return (
      <div>
        <h3>Your Order</h3>
        <p>A delicious Krabby Patty with the following ingredients:</p>
        <ul className='order-sum-list'>
          {
            ingredientsArr.map((key, index) =>
              <li key={index}>
                <span className='order-sum-ingre-name'>{key}</span>: {this.props.ingredients[key]}
              </li>
            )
          }
        </ul>
        <p><strong>Total Price: ${this.props.totalPrice.toFixed(2)}</strong></p>
        <p>Continue to check out?</p>
        <button
          className='btn order-cancel-btn'
          onClick={() => this.props.closeModalHandler()}>CANCEL</button>
        <button
          className='btn order-continue-btn'
          onClick={() => this.props.purchaseContinueHandler()}>CONTINUE</button>
      </div >
    )
  }

}

export default OrderSummary;
