import React from 'react'

const OrderSummary = (props) => {

  // The Object.keys() method converts an object to an array of keys
  const ingredientsArr = Object.keys(props.ingredients)

  return (
    <>
      <h3>Your Order</h3>
      <div>A delicious Krabby Patty with the following ingredients:</div>
      <ul>
        {
          ingredientsArr.map(key =>
            <li><span className='order-sum-ingre-name'>{key}</span>: {props.ingredients[key]}</li>
          )
        }
      </ul>
    </>
  )
}

export default OrderSummary;
