import React from 'react'
import './Burger.css'
import BurgerIngredient from './BurgerIngredient/BurgerIngredient'

const Burger = (props) => {

  // The Object.keys method will turn an object into an array of its keys
  const ingredientsArr = Object.keys(props.ingredients)  // [meat, salad, bacon]

  // Array() method creates an array with specified number of empty values [ , ] 
  // We don't care about what those values are, we just need an array with the right length to use 
  // map()
  const transformedIngredients = ingredientsArr.map(name => 
    Array(props.ingredients[name])  
      .map( (x, index) => {
        <BurgerIngredient key={name + index} type={name} />
      }))  


  return (
    <div className='burger'>
      <BurgerIngredient type='breadTop' />
      <BurgerIngredient type='cheese' />
      <BurgerIngredient type='meat' />
      <BurgerIngredient type='breadBottom' />
    </div>
  )
}



export default Burger;
