import React from 'react'
import './Backdrop.css'

const Backdrop = (props) => {

  let backdrop = null

  if (props.isPurchasing || props.isMobile) {
    backdrop = <div
      className='backdrop'
      onClick={() => props.closeModalHandler()}
    ></div>
  }

  return (
    backdrop
  )
}


export default Backdrop;
